import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { registerUser } from '../services/api';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    password2: '',
    first_name: '',
    last_name: ''
  });
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.username) {
      newErrors.username = 'Username is required';
    }

    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    if (formData.password !== formData.password2) {
      newErrors.password2 = 'Passwords do not match';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form
    if (!validateForm()) {
      return;
    }

    try {
      setLoading(true);

      // Remove password confirmation from data sent to server
      const { password2, ...registrationData } = formData;

      // For development: Use localStorage instead of actual API call
      // This way we can test the frontend without backend connectivity
      try {
        // First try to make the API call
        await axios.post('/api/auth/register/', registrationData);
      } catch (apiError) {
        console.log('API error, using localStorage fallback for development');
        // Store user in localStorage for development purposes
        let users = [];
        try {
          const usersData = localStorage.getItem('users');
          if (usersData) {
            const parsedUsers = JSON.parse(usersData);
            if (Array.isArray(parsedUsers)) {
              users = parsedUsers;
            } else {
              // Convert old format if needed (object to array)
              users = Object.entries(parsedUsers).map(([username, password]) => ({
                id: Math.floor(Math.random() * 1000),
                username,
                password,
                email: `${username}@example.com`,
                date_joined: new Date().toISOString()
              }));
            }
          }
        } catch (e) {
          console.error('Error parsing users from localStorage:', e);
          users = [];
        }

        // Check if username already exists
        if (users.some(user => user.username === formData.username)) {
          throw new Error('Username already exists');
        }

        // Check if email already exists
        if (users.some(user => user.email === formData.email)) {
          throw new Error('Email already exists');
        }

        // Add new user
        users.push({
          ...registrationData,
          id: Date.now(),
          date_joined: new Date().toISOString()
        });

        localStorage.setItem('users', JSON.stringify(users));
      }

      // Redirect to login page after successful registration
      alert('Registration successful! Please log in.');
      navigate('/login');

    } catch (error) {
      console.error('Registration error:', error);

      if (error.response && error.response.data) {
        // Format backend validation errors
        setErrors(error.response.data);
      } else if (error.message === 'Username already exists') {
        setErrors({
          username: ['This username is already taken.']
        });
      } else if (error.message === 'Email already exists') {
        setErrors({
          email: ['This email is already registered.']
        });
      } else {
        setErrors({
          non_field_errors: ['An error occurred during registration. Please try again.']
        });
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="auth-form mt-5">
        <h2 className="text-center mb-4">Register</h2>

        {errors.non_field_errors && (
          <div className="alert alert-danger" role="alert">
            {errors.non_field_errors}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="username" className="form-label">Username</label>
            <input
              type="text"
              className={`form-control ${errors.username ? 'is-invalid' : ''}`}
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
            />
            {errors.username && (
              <div className="invalid-feedback">{errors.username}</div>
            )}
          </div>

          <div className="mb-3">
            <label htmlFor="email" className="form-label">Email</label>
            <input
              type="email"
              className={`form-control ${errors.email ? 'is-invalid' : ''}`}
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
            />
            {errors.email && (
              <div className="invalid-feedback">{errors.email}</div>
            )}
          </div>

          <div className="row">
            <div className="col-md-6 mb-3">
              <label htmlFor="first_name" className="form-label">First Name</label>
              <input
                type="text"
                className="form-control"
                id="first_name"
                name="first_name"
                value={formData.first_name}
                onChange={handleChange}
              />
            </div>

            <div className="col-md-6 mb-3">
              <label htmlFor="last_name" className="form-label">Last Name</label>
              <input
                type="text"
                className="form-control"
                id="last_name"
                name="last_name"
                value={formData.last_name}
                onChange={handleChange}
              />
            </div>
          </div>

          <div className="mb-3">
            <label htmlFor="password" className="form-label">Password</label>
            <input
              type="password"
              className={`form-control ${errors.password ? 'is-invalid' : ''}`}
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
            />
            {errors.password && (
              <div className="invalid-feedback">{errors.password}</div>
            )}
          </div>

          <div className="mb-3">
            <label htmlFor="password2" className="form-label">Confirm Password</label>
            <input
              type="password"
              className={`form-control ${errors.password2 ? 'is-invalid' : ''}`}
              id="password2"
              name="password2"
              value={formData.password2}
              onChange={handleChange}
            />
            {errors.password2 && (
              <div className="invalid-feedback">{errors.password2}</div>
            )}
          </div>

          <div className="d-grid gap-2">
            <button 
              type="submit" 
              className="btn btn-primary"
              disabled={loading}
            >
              {loading ? 'Registering...' : 'Register'}
            </button>
          </div>
        </form>

        <div className="text-center mt-3">
          <p>Already have an account? <Link to="/login">Login</Link></p>
        </div>
      </div>
    </div>
  );
};

export default Register;
