import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import '../../styles/main.css';

const Header = () => {
  const [user, setUser] = useState({});
  const [currentDate, setCurrentDate] = useState('');

  useEffect(() => {
    // Get user info from localStorage
    const userInfo = JSON.parse(localStorage.getItem('loggedInUser') || '{"username": "Admin"}')
    setUser(userInfo);

    // Set current date
    const date = new Date();
    setCurrentDate(date.toLocaleDateString('en-US', { 
      weekday: 'long', 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    }));
  }, []);

  return (
    <header className="dashboard-card-header p-3 mb-4">
      <div className="container-fluid">
        <div className="d-flex justify-content-between align-items-center">
          <div>
            <p className="text-muted mb-0">{currentDate}</p>
          </div>

          <div className="d-flex align-items-center">
            <div className="me-3">
              <span className="badge bg-success">Online</span>
            </div>
            <div className="d-flex align-items-center">
              <div className="me-2" style={{ 
                width: '40px', 
                height: '40px', 
                borderRadius: '50%', 
                backgroundColor: 'var(--accent-color)', 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'center', 
                color: 'white',
                fontWeight: 'bold'
              }}>
                {(user.username || 'A').charAt(0).toUpperCase()}
              </div>
              <div>
                <h6 className="mb-0">{user.username || 'Admin'}</h6>
                <small className="text-muted">Administrator</small>
              </div>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
