import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DashboardStats = () => {
  const [stats, setStats] = useState({
    totalOrders: 0,
    pendingOrders: 0,
    totalProducts: 0,
    totalUsers: 0,
    recentOrders: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // In a real application, you would fetch this data from your API
    // For now, we'll use mock data
    const fetchStats = async () => {
      try {
        setLoading(true);

        // This would be an actual API call in a real application
        // const response = await axios.get('/api/admin/stats/');
        // setStats(response.data);

        // Mock data for demonstration
        setTimeout(() => {
          setStats({
            totalOrders: 124,
            pendingOrders: 18,
            totalProducts: 45,
            totalUsers: 78,
            recentOrders: [
              { id: 1, user: 'johndoe', price: 29.99, date: '2025-05-22', status: 'Delivered' },
              { id: 2, user: 'janedoe', price: 42.50, date: '2025-05-22', status: 'Pending' },
              { id: 3, user: 'bobsmith', price: 18.75, date: '2025-05-21', status: 'Delivered' },
              { id: 4, user: 'alicejones', price: 35.25, date: '2025-05-21', status: 'Pending' },
              { id: 5, user: 'mikebrown', price: 24.00, date: '2025-05-20', status: 'Delivered' }
            ]
          });
          setLoading(false);
        }, 1000);

      } catch (error) {
        console.error('Error fetching admin stats:', error);
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  if (loading) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div>
      <h2 className="mb-4">Dashboard</h2>

      <div className="row">
        <div className="col-md-3 mb-4">
          <div className="card bg-primary text-white h-100">
            <div className="card-body">
              <h5 className="card-title">Total Orders</h5>
              <h2 className="display-4">{stats.totalOrders}</h2>
            </div>
            <div className="card-footer d-flex">
              View Details
              <span className="ms-auto">
                <i className="bi bi-chevron-right"></i>
              </span>
            </div>
          </div>
        </div>

        <div className="col-md-3 mb-4">
          <div className="card bg-warning text-white h-100">
            <div className="card-body">
              <h5 className="card-title">Pending Orders</h5>
              <h2 className="display-4">{stats.pendingOrders}</h2>
            </div>
            <div className="card-footer d-flex">
              View Details
              <span className="ms-auto">
                <i className="bi bi-chevron-right"></i>
              </span>
            </div>
          </div>
        </div>

        <div className="col-md-3 mb-4">
          <div className="card bg-success text-white h-100">
            <div className="card-body">
              <h5 className="card-title">Total Products</h5>
              <h2 className="display-4">{stats.totalProducts}</h2>
            </div>
            <div className="card-footer d-flex">
              View Details
              <span className="ms-auto">
                <i className="bi bi-chevron-right"></i>
              </span>
            </div>
          </div>
        </div>

        <div className="col-md-3 mb-4">
          <div className="card bg-danger text-white h-100">
            <div className="card-body">
              <h5 className="card-title">Total Users</h5>
              <h2 className="display-4">{stats.totalUsers}</h2>
            </div>
            <div className="card-footer d-flex">
              View Details
              <span className="ms-auto">
                <i className="bi bi-chevron-right"></i>
              </span>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        <div className="col-12">
          <div className="card mb-4">
            <div className="card-header">
              <i className="bi bi-table me-1"></i>
              Recent Orders
            </div>
            <div className="card-body">
              <div className="table-responsive">
                <table className="table table-bordered">
                  <thead>
                    <tr>
                      <th>Order ID</th>
                      <th>Customer</th>
                      <th>Amount</th>
                      <th>Date</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {stats.recentOrders.map(order => (
                      <tr key={order.id}>
                        <td>#{order.id}</td>
                        <td>{order.user}</td>
                        <td>${order.price.toFixed(2)}</td>
                        <td>{order.date}</td>
                        <td>
                          <span className={`badge ${order.status === 'Delivered' ? 'bg-success' : 'bg-warning'}`}>
                            {order.status}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DashboardStats;
