import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.username || !formData.password) {
      setError('Please enter both username and password');
      return;
    }

    try {
      setLoading(true);
      setError('');

      try {
        // First try the backend API
        const response = await axios.post('/api/auth/login/', formData);

        // Store token and user info in localStorage
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data.user));
      } catch (apiError) {
        console.log('API error, using localStorage fallback for development');

        // For development: Use localStorage for authentication when API fails
        let users = [];
        try {
          const usersData = localStorage.getItem('users');
          if (usersData) {
            const parsedUsers = JSON.parse(usersData);
            if (Array.isArray(parsedUsers)) {
              users = parsedUsers;
            }
          }
        } catch (e) {
          console.error('Error parsing users from localStorage:', e);
          throw new Error('An error occurred during login');
        }

        // Find user with matching credentials
        const user = users.find(u => 
          u.username === formData.username && u.password === formData.password
        );

        if (!user) {
          throw new Error('Invalid username or password');
        }

        // Store user info in localStorage
        localStorage.setItem('currentUser', JSON.stringify({
          id: user.id,
          username: user.username,
          email: user.email,
          first_name: user.first_name || '',
          last_name: user.last_name || '',
          is_authenticated: true
        }));

        // Initialize empty cart if needed
        if (!localStorage.getItem('cart')) {
          localStorage.setItem('cart', JSON.stringify([]));
        }
      }

      // Redirect to home page
      navigate('/');

    } catch (error) {
      console.error('Login error:', error);

      if (error.response && error.response.data) {
        setError(error.response.data.detail || 'Invalid username or password');
      } else if (error.message === 'Invalid username or password') {
        setError('Invalid username or password');
      } else {
        setError('An error occurred during login. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="auth-form mt-5">
        <h2 className="text-center mb-4">Login</h2>

        {error && (
          <div className="alert alert-danger" role="alert">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="username" className="form-label">Username</label>
            <input
              type="text"
              className="form-control"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="password" className="form-label">Password</label>
            <input
              type="password"
              className="form-control"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>

          <div className="d-grid gap-2">
            <button 
              type="submit" 
              className="btn btn-primary"
              disabled={loading}
            >
              {loading ? 'Logging in...' : 'Login'}
            </button>
          </div>
        </form>

        <div className="text-center mt-3">
          <p>Don't have an account? <Link to="/register">Register</Link></p>
        </div>
      </div>
    </div>
  );
};

export default Login;
