import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/main.css';

const LoginFr = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // For development purposes, using localStorage for authentication
    // In production, you would use your Django backend API
    try {
      // Get users from localStorage (now an array after our Register component update)
      const users = JSON.parse(localStorage.getItem('users') || '[]');

      // Find user by username
      const user = users.find(u => u.username === formData.username);

      if (user && user.password === formData.password) {
        // Store user info in localStorage
        localStorage.setItem('currentUser', JSON.stringify({
          id: user.id,
          username: user.username,
          email: user.email,
          first_name: user.first_name,
          last_name: user.last_name,
          is_authenticated: true
        }));

        // Initialize empty cart if needed
        if (!localStorage.getItem('cart')) {
          localStorage.setItem('cart', JSON.stringify([]));
        }

        navigate('/'); // Redirect to home page
      } else {
        setErrorMessage("Nom d'utilisateur ou mot de passe incorrect.");
      }
    } catch (error) {
      console.error('Login error:', error);
      setErrorMessage('Une erreur est survenue. Veuillez réessayer.');
    }
  };

  // Initialize default users if none exist (for development)
  React.useEffect(() => {
    const users = JSON.parse(localStorage.getItem('users') || '[]');

    if (users.length === 0) {
      const defaultUsers = [
        {
          id: 1,
          username: 'admin',
          password: 'admin123',
          email: 'admin@example.com',
          first_name: 'Admin',
          last_name: 'User',
          date_joined: new Date().toISOString(),
          is_staff: true
        },
        {
          id: 2,
          username: 'user1',
          password: 'password123',
          email: 'user1@example.com',
          first_name: 'Regular',
          last_name: 'User',
          date_joined: new Date().toISOString(),
          is_staff: false
        }
      ];
      localStorage.setItem('users', JSON.stringify(defaultUsers));
    }
  }, []);

  return (
    <div className="container">
      <div className="login-container">
        <h2>Welcome Back</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="username">Nom d'utilisateur</label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Mot de passe</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          {errorMessage && (
            <div className="alert alert-danger" role="alert">
              {errorMessage}
            </div>
          )}
          <button type="submit">Se connecter</button>
          <div className="register-link">
            Vous n'avez pas de compte? <Link to="/register">Inscrivez-vous ici</Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default LoginFr;
