import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/main.css';

const LoginFr = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // For development purposes, using localStorage for authentication
    // In production, you would use your Django backend API
    const users = JSON.parse(localStorage.getItem('users')) || {};

    if (users[formData.username] && users[formData.username] === formData.password) {
      localStorage.setItem('loggedInUser', formData.username);
      navigate('/'); // Redirect to home page
    } else {
      setErrorMessage("Nom d'utilisateur ou mot de passe incorrect.");
    }
  };

  // Initialize default users if none exist (for development)
  React.useEffect(() => {
    if (!localStorage.getItem('users')) {
      const defaultUsers = {
        'admin': 'admin123', // Admin credentials
        'user1': 'password123'
      };
      localStorage.setItem('users', JSON.stringify(defaultUsers));
    }
  }, []);

  return (
    <div className="container">
      <div className="login-container">
        <h2>Welcome Back</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="username">Nom d'utilisateur</label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Mot de passe</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          {errorMessage && (
            <div className="alert alert-danger" role="alert">
              {errorMessage}
            </div>
          )}
          <button type="submit">Se connecter</button>
          <div className="register-link">
            Vous n'avez pas de compte? <Link to="/register">Inscrivez-vous ici</Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default LoginFr;
