import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const LoginFr = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // For development purposes, using localStorage for authentication
    // In production, you would use your Django backend API
    const users = JSON.parse(localStorage.getItem('users')) || {};

    if (users[formData.username] && users[formData.username] === formData.password) {
      localStorage.setItem('loggedInUser', formData.username);
      navigate('/'); // Redirect to home page
    } else {
      setErrorMessage("Nom d'utilisateur ou mot de passe incorrect.");
    }
  };

  // Initialize default users if none exist (for development)
  React.useEffect(() => {
    if (!localStorage.getItem('users')) {
      const defaultUsers = {
        'admin': 'admin123', // Admin credentials
        'user1': 'password123'
      };
      localStorage.setItem('users', JSON.stringify(defaultUsers));
    }
  }, []);

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6 col-lg-4">
          <div className="card shadow">
            <div className="card-body p-4">
              <h2 className="text-center mb-4">Connexion</h2>

              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label htmlFor="username" className="form-label">Nom d'utilisateur</label>
                  <input
                    type="text"
                    className="form-control"
                    id="username"
                    name="username"
                    value={formData.username}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="password" className="form-label">Mot de passe</label>
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="d-grid">
                  <button type="submit" className="btn btn-primary">Se connecter</button>
                </div>

                {errorMessage && (
                  <div className="alert alert-danger mt-3" role="alert">
                    {errorMessage}
                  </div>
                )}

                <p className="text-center mt-3">
                  Vous n'avez pas de compte? <Link to="/register">Inscrivez-vous ici</Link>.
                </p>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginFr;
