import React, { useState, useEffect } from 'react';
import { Navigate } from 'react-router-dom';
import Sidebar from '../../components/admin/Sidebar';
import Header from '../../components/admin/Header';
import OrdersList from '../../components/admin/OrdersList';
import ProductsList from '../../components/admin/ProductsList';
import UsersList from '../../components/admin/UsersList';
import DashboardStats from '../../components/admin/DashboardStats';
import '../../styles/main.css';
import '../../styles/admin.css';

const Dashboard = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);
  const [activeComponent, setActiveComponent] = useState('dashboard');

  useEffect(() => {
    // Check if user is authenticated and is admin
    const currentUser = JSON.parse(localStorage.getItem('currentUser') || '{}');

    if (currentUser && currentUser.is_authenticated) {
      setIsAuthenticated(true);

      // Check if the user has admin privileges (username is 'admin' or is_staff is true)
      if (currentUser.username === 'admin' || currentUser.is_staff === true) {
        setIsAdmin(true);
      } else {
        setIsAdmin(false);
      }
    } else {
      setIsAuthenticated(false);
      setIsAdmin(false);
    }

    setLoading(false);
  }, []);

  // Redirect to login if not authenticated or not admin
  if (!loading && (!isAuthenticated || !isAdmin)) {
    return <Navigate to="/login-fr" />;
  }

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
        <div className="spinner-border" style={{ color: 'var(--accent-color)' }} role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  // Render the appropriate component based on the active menu item
  const renderComponent = () => {
    switch(activeComponent) {
      case 'dashboard':
        return <DashboardStats />;
      case 'orders':
        return <OrdersList />;
      case 'products':
        return <ProductsList />;
      case 'users':
        return <UsersList />;
      default:
        return <DashboardStats />;
    }
  };

  return (
    <div className="dashboard-container">
      <Sidebar activeComponent={activeComponent} setActiveComponent={setActiveComponent} />

      <div className="dashboard-content">
        <Header />

        <main className="p-4">
          <div className="container-fluid">
            <div className="dashboard-header mb-4">
              <h1 className="mb-0" style={{ fontFamily: 'var(--heading-font)', color: 'var(--heading-color)' }}>
                {activeComponent.charAt(0).toUpperCase() + activeComponent.slice(1)}                
              </h1>
              <p className="text-muted">Manage your restaurant with ease</p>
            </div>

            <div className="dashboard-card">
              {renderComponent()}
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
