import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Sidebar = ({ activeComponent, setActiveComponent }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Clear local storage
    localStorage.removeItem('token');
    localStorage.removeItem('user');

    // Redirect to login page
    navigate('/login');
  };

  return (
    <div className="sidebar bg-dark text-white" style={{ width: '250px', minHeight: '100vh' }}>
      <div className="p-3 border-bottom">
        <h4 className="text-center">Admin Dashboard</h4>
      </div>

      <ul className="nav flex-column mt-4">
        <li className="nav-item">
          <button 
            className={`nav-link text-white border-0 bg-transparent text-start w-100 ${activeComponent === 'dashboard' ? 'active bg-primary' : ''}`}
            onClick={() => setActiveComponent('dashboard')}
          >
            <i className="bi bi-speedometer2 me-2"></i> Dashboard
          </button>
        </li>
        <li className="nav-item">
          <button 
            className={`nav-link text-white border-0 bg-transparent text-start w-100 ${activeComponent === 'orders' ? 'active bg-primary' : ''}`}
            onClick={() => setActiveComponent('orders')}
          >
            <i className="bi bi-cart-check me-2"></i> Orders
          </button>
        </li>
        <li className="nav-item">
          <button 
            className={`nav-link text-white border-0 bg-transparent text-start w-100 ${activeComponent === 'products' ? 'active bg-primary' : ''}`}
            onClick={() => setActiveComponent('products')}
          >
            <i className="bi bi-box me-2"></i> Products
          </button>
        </li>
        <li className="nav-item">
          <button 
            className={`nav-link text-white border-0 bg-transparent text-start w-100 ${activeComponent === 'users' ? 'active bg-primary' : ''}`}
            onClick={() => setActiveComponent('users')}
          >
            <i className="bi bi-people me-2"></i> Users
          </button>
        </li>
        <li className="nav-item mt-3">
          <Link className="nav-link text-white" to="/">
            <i className="bi bi-house me-2"></i> Back to Site
          </Link>
        </li>
        <li className="nav-item">
          <button 
            className="nav-link text-white border-0 bg-transparent text-start w-100"
            onClick={handleLogout}
          >
            <i className="bi bi-box-arrow-right me-2"></i> Logout
          </button>
        </li>
      </ul>
    </div>
  );
};

export default Sidebar;
