import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Toast, ToastContainer } from 'react-bootstrap';
import Payment from '../components/Payment';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState('');
  const [toastType, setToastType] = useState('success');
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [totalWithTax, setTotalWithTax] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    // Load cart items from localStorage
    const loadCart = () => {
      const cart = JSON.parse(localStorage.getItem('cart') || '[]');
      setCartItems(cart);

      // Calculate total
      const cartTotal = cart.reduce((sum, item) => {
        return sum + (item.price * item.quantity);
      }, 0);

      setTotal(cartTotal);
    };

    loadCart();

    // Listen for cart updates
    window.addEventListener('storage', loadCart);
    return () => window.removeEventListener('storage', loadCart);
  }, []);

  const updateQuantity = (index, newQuantity) => {
    if (newQuantity < 1) return;

    const updatedCart = [...cartItems];
    updatedCart[index].quantity = newQuantity;
    setCartItems(updatedCart);

    // Update localStorage
    localStorage.setItem('cart', JSON.stringify(updatedCart));

    // Recalculate total
    const cartTotal = updatedCart.reduce((sum, item) => {
      return sum + (item.price * item.quantity);
    }, 0);

    setTotal(cartTotal);
  };

  const removeItem = (index) => {
    const updatedCart = [...cartItems];
    const removedItem = updatedCart[index];
    updatedCart.splice(index, 1);
    setCartItems(updatedCart);

    // Update localStorage
    localStorage.setItem('cart', JSON.stringify(updatedCart));

    // Trigger storage event for navbar to update cart count
    window.dispatchEvent(new Event('storage'));

    // Recalculate total
    const cartTotal = updatedCart.reduce((sum, item) => {
      return sum + (item.price * item.quantity);
    }, 0);

    setTotal(cartTotal);

    // Show toast notification
    setToastMessage(`Removed ${removedItem.description} from cart`);
    setToastType('warning');
    setShowToast(true);
  };

  const clearCart = () => {
    setCartItems([]);
    localStorage.removeItem('cart');

    // Trigger storage event for navbar to update cart count
    window.dispatchEvent(new Event('storage'));

    setTotal(0);
  };

  const checkout = async () => {
    // Check for authentication using the currentUser key that we're now using
    const currentUser = localStorage.getItem('currentUser');

    if (!currentUser) {
      setToastMessage('Please login to place an order');
      setToastType('danger');
      setShowToast(true);
      setTimeout(() => navigate('/login-fr'), 2000);
      return;
    }

    if (cartItems.length === 0) {
      setToastMessage('Your cart is empty');
      setToastType('warning');
      setShowToast(true);
      return;
    }

    try {
      setLoading(true);

      // Format order data
      const orderData = {
        customer: JSON.parse(currentUser).username,
        items: cartItems.map(item => ({
          description: item.description,
          price: item.price,
          quantity: item.quantity
        })),
        total: total,
        status: 'Pending'
      };

      // In a real application, this would send the data to the backend
      // await axios.post('/api/orders/', orderData);

      // For now, we'll simulate a successful order by storing it in localStorage
      const orders = JSON.parse(localStorage.getItem('orders') || '[]');
      const newOrder = {
        id: Date.now(),
        date: new Date().toISOString(),
        ...orderData
      };
      orders.push(newOrder);
      localStorage.setItem('orders', JSON.stringify(orders));

      // Clear cart after successful order
      clearCart();

      setToastMessage('Order placed successfully!');
      setToastType('success');
      setShowToast(true);

      setTimeout(() => navigate('/orders'), 2000);

    } catch (error) {
      console.error('Error placing order:', error);
      setToastMessage('Failed to place order. Please try again.');
      setToastType('danger');
      setShowToast(true);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h1 className="mb-4">Your Cart</h1>

      {cartItems.length === 0 ? (
        <div className="text-center py-5">
          <div className="card shadow-sm p-5">
            <div className="card-body">
              <i className="bi bi-cart-x" style={{ fontSize: '4rem', color: '#ccc' }}></i>
              <h3 className="mt-3">Your cart is empty</h3>
              <p className="text-muted">Add some delicious items to your cart and come back!</p>
              <div className="mt-4">
                <Link to="/menu-categories" className="btn btn-primary me-2">Browse Menu</Link>
                <Link to="/pizza" className="btn btn-outline-primary">Order Pizza</Link>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <>
          <div className="card shadow-sm mb-4">
            <div className="card-header bg-light">
              <h5 className="mb-0">Cart Items</h5>
            </div>
            <div className="card-body">
              <div className="table-responsive">
                <table className="table">
                  <thead>
                    <tr>
                      <th>Item</th>
                      <th>Price</th>
                      <th>Quantity</th>
                      <th>Subtotal</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {cartItems.map((item, index) => (
                      <tr key={index}>
                        <td>{item.description}</td>
                        <td>${item.price.toFixed(2)}</td>
                        <td>
                          <div className="input-group input-group-sm" style={{ width: '120px' }}>
                            <button 
                              className="btn btn-outline-secondary" 
                              type="button"
                              onClick={() => updateQuantity(index, item.quantity - 1)}
                            >
                              <i className="bi bi-dash"></i>
                            </button>
                            <input 
                              type="number" 
                              className="form-control text-center" 
                              value={item.quantity}
                              readOnly
                            />
                            <button 
                              className="btn btn-outline-secondary" 
                              type="button"
                              onClick={() => updateQuantity(index, item.quantity + 1)}
                            >
                              <i className="bi bi-plus"></i>
                            </button>
                          </div>
                        </td>
                        <td>${(item.price * item.quantity).toFixed(2)}</td>
                        <td>
                          <button 
                            className="btn btn-sm btn-outline-danger"
                            onClick={() => removeItem(index)}
                          >
                            <i className="bi bi-trash"></i>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>

          <div className="row mb-4">
            <div className="col-md-6">
              <div className="card shadow-sm">
                <div className="card-header bg-light">
                  <h5 className="mb-0">Order Summary</h5>
                </div>
                <div className="card-body">
                  <div className="d-flex justify-content-between mb-2">
                    <span>Subtotal:</span>
                    <span>${total.toFixed(2)}</span>
                  </div>
                  <div className="d-flex justify-content-between mb-2">
                    <span>Delivery:</span>
                    <span>$0.00</span>
                  </div>
                  <div className="d-flex justify-content-between mb-2">
                    <span>Tax:</span>
                    <span>${(total * 0.08).toFixed(2)}</span>
                  </div>
                  <hr />
                  <div className="d-flex justify-content-between fw-bold">
                    <span>Total:</span>
                    <span>${(total + (total * 0.08)).toFixed(2)}</span>
                  </div>
                </div>
              </div>
            </div>

            <div className="col-md-6">
              <div className="card shadow-sm">
                <div className="card-header bg-light">
                  <h5 className="mb-0">Actions</h5>
                </div>
                <div className="card-body">
                  <div className="d-grid gap-2">
                    <button 
                      className="btn btn-success"
                      onClick={checkout}
                      disabled={loading}
                    >
                      {loading ? (
                        <>
                          <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                          Processing...
                        </>
                      ) : (
                        <>
                          <i className="bi bi-credit-card me-2"></i> Checkout
                        </>
                      )}
                    </button>

                    <Link to="/menu-categories" className="btn btn-outline-primary">
                      <i className="bi bi-arrow-left me-2"></i> Continue Shopping
                    </Link>

                    <button 
                      className="btn btn-outline-danger"
                      onClick={clearCart}
                    >
                      <i className="bi bi-trash me-2"></i> Clear Cart
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </>
      )}

      <ToastContainer position="bottom-end" className="p-3" style={{ zIndex: 1070 }}>
        <Toast 
          show={showToast} 
          onClose={() => setShowToast(false)} 
          delay={3000} 
          autohide 
          bg={toastType}
        >
          <Toast.Header>
            <strong className="me-auto">{toastType === 'success' ? 'Success' : toastType === 'danger' ? 'Error' : 'Notice'}</strong>
          </Toast.Header>
          <Toast.Body className={toastType === 'danger' ? 'text-white' : ''}>
            {toastMessage}
          </Toast.Body>
        </Toast>
      </ToastContainer>
    </div>
  );
};

export default Cart;
